import React from "react";
import { Grid, Typography, Box, Button } from "@material-ui/core";

import { makeStyles } from "@material-ui/core/styles";

import { MuiThemeProvider } from "@material-ui/core/styles";

import { theme } from "../../theme";

const useStyles = makeStyles((theme) => ({
  hero: {
    paddingTop: theme.spacing(4),
    paddingBottom: theme.spacing(4),
  },
  screen: {
    maxHeight: 500,
  },
  messageTop: {
    paddingBottom: theme.spacing(2),
  },
  button: {
    width: 240,
    height: 56,
    borderRadius: 40,
    marginTop: theme.spacing(2),
    color: "#ffffff",
    backgroundColor: "#1dcd00",
    "&:hover": {
      backgroundColor: "#1dcd00",
    },
  },
}));

const Hero = () => {
  const classes = useStyles();

  return (
    <div>
      <MuiThemeProvider theme={theme}>
        <Grid
          container
          direction="row-reverse"
          justify="center"
          alignItems="center"
          className={classes.hero}
        >
          <Grid item xs={12} md={6}>
            <Typography variant="h4" gutterBottom>
              <Box fontWeight="fontWeightBold" className={classes.messageTop}>
                たくさん話せて
              </Box>
              <Box fontWeight="fontWeightBold">たくさん笑えるトークゲーム</Box>
            </Typography>
            <Typography variant="body1">
              Minigame
              Dabyss(ダビス)は、コミュニケーションの可能性を無限にする複数のゲームを収録したLINE
              botのサービスです。
            </Typography>
            <a href="https://line.me/R/ti/p/@390zkjvc">
              <Button variant="contained" className={classes.button}>
                <Typography variant="h6">LINEで友だち追加</Typography>
              </Button>
            </a>
          </Grid>
          <Grid item md={2}></Grid>
          <Grid item xs={12} md={5}>
            <img src="./screen.png" className={classes.screen}></img>
          </Grid>
        </Grid>
      </MuiThemeProvider>
    </div>
  );
};
export default Hero;
