import React, { Fragment, useState, useEffect } from "react";
import GameCard from "./GameCard";
import axios from "axios";

import { Link } from "react-router-dom";

import { Typography, Button, Grid, Box } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { MuiThemeProvider } from "@material-ui/core/styles";
import { theme } from "../theme";

const useStyles = makeStyles((theme) => ({
  title: {
    paddingTop: theme.spacing(4),
    paddingBottom: theme.spacing(4),
  },
  card: {
    maxWidth: 345,
    margin: "auto",
  },
  button: {
    paddingTop: theme.spacing(4),
    paddingBottom: theme.spacing(4),
  },
}));

const TopGames = () => {
  const classes = useStyles();
  const [articles, setArticles] = useState([]);
  useEffect(async () => {
    const res = await axios.get("http://localhost:8000/api/games/");
    setArticles(res.data);
  }, []);
  return (
    <div>
      {/* <Fragment> */}
      {articles.games.map((article) => (
        <div>
          <h1>{article.title}</h1>
          <img src={article.sumnail}></img>
          {/* <p>{article.sumnail}</p> */}
        </div>
      ))}
      {/* </Fragment> */}
      <MuiThemeProvider theme={theme}>
        <Grid container justify="center" className={classes.title}>
          <Typography variant="h4">
            <Box fontWeight="fontWeightBold">ゲーム一覧</Box>
          </Typography>
        </Grid>
        <Grid container>
          <Grid item xs={4}>
            <GameCard
              image="./logo_Dabyss.png"
              name="hoge"
              detail="hogehoge"
            ></GameCard>
          </Grid>
          <Grid item xs={4}>
            <GameCard
              image="./logo_Dabyss.png"
              name="hoge"
              detail="pagepage"
            ></GameCard>
          </Grid>
          <Grid item xs={4}>
            <GameCard
              image="./logo_Dabyss.png"
              name="hoge"
              detail="Lizards are a widespread group of squamate reptiles, with over
              6,000 species, ranging across all continents except Antarctica"
            ></GameCard>
          </Grid>
        </Grid>
        <Grid container justify="center" className={classes.button}>
          <Link to="/Games">
            <Button variant="outlined" color="secondary">
              more
            </Button>
          </Link>
        </Grid>
      </MuiThemeProvider>
    </div>
  );
};

export default TopGames;
