import React from "react";

import { Link } from "react-router-dom";

import {
  AppBar,
  Toolbar,
  Typography,
  Button,
  Grid,
  Box,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { MuiThemeProvider } from "@material-ui/core/styles";
import { theme } from "../theme";

import PropTypes from "prop-types";
import CssBaseline from "@material-ui/core/CssBaseline";
import Divider from "@material-ui/core/Divider";
import Drawer from "@material-ui/core/Drawer";
import Hidden from "@material-ui/core/Hidden";
import IconButton from "@material-ui/core/IconButton";
import InboxIcon from "@material-ui/icons/MoveToInbox";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import MailIcon from "@material-ui/icons/Mail";
import MenuIcon from "@material-ui/icons/Menu";

import logoSrc from "../assets/logo_Dabyss.png";

const useStyles = makeStyles((theme) => ({
  header: {
    height: 80,
  },
  logo: {
    maxWidth: 60,
  },
  games: {
    padding: theme.spacing(2),
    fontWeight: "bold",
  },
  blog: {
    padding: theme.spacing(2),
    fontWeight: "bold",
  },
  contact: {
    padding: theme.spacing(2),
    fontWeight: "bold",
  },
}));

const Header = (props) => {
  const classes = useStyles();
  const { window } = props;
  const [mobileOpen, setMobileOpen] = React.useState(false);

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  const drawer = (
    <div>
      <div className={classes.toolbar} />
      <List>
        {["Inbox", "Starred", "Send email", "Drafts"].map((text, index) => (
          <ListItem button key={text}>
            <ListItemIcon>
              {index % 2 === 0 ? <InboxIcon /> : <MailIcon />}
            </ListItemIcon>
            <ListItemText primary={text} />
          </ListItem>
        ))}
      </List>
    </div>
  );

  // const container =
  //   window !== undefined ? () => window().document.body : undefined;

  return (
    <div>
      <MuiThemeProvider theme={theme}>
        <AppBar position="static">
          <Toolbar className={classes.header}>
            <Box display="flex" justifyContent="flex-start" flexGrow={1}>
              <Link to="/">
                <Grid container alignItems="center">
                  <img src={logoSrc} className={classes.logo} />
                  <Typography variant="h6" className={classes.title}>
                    MinigameDabyss
                  </Typography>
                </Grid>
              </Link>
            </Box>
            <Hidden xsDown implementation="css">
              <Box display="flex" justifyContent="flex-end">
                <Link to="/Games">
                  <Button color="inherit" className={classes.games}>
                    ゲーム一覧
                  </Button>
                </Link>
                <Link to="/Blog">
                  <Button color="inherit" className={classes.blog}>
                    ブログ
                  </Button>
                </Link>
                <Button color="inherit" className={classes.contact}>
                  お問い合わせ
                </Button>
              </Box>
            </Hidden>
            <Hidden smUp implementation="css">
              <IconButton
                color="inherit"
                aria-label="open drawer"
                edge="start"
                onClick={handleDrawerToggle}
                className={classes.menuButton}
              >
                <MenuIcon />
              </IconButton>
            </Hidden>
          </Toolbar>
        </AppBar>
      </MuiThemeProvider>
    </div>
  );
};

export default Header;
