import React from "react";

import { Link } from "react-router-dom";

import {
  AppBar,
  Toolbar,
  Typography,
  Button,
  Grid,
  Box,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { MuiThemeProvider } from "@material-ui/core/styles";
import { theme } from "../theme";

import Hidden from "@material-ui/core/Hidden";
import IconButton from "@material-ui/core/IconButton";
import MenuIcon from "@material-ui/icons/Menu";
import Modal from "@material-ui/core/Modal";
import Backdrop from "@material-ui/core/Backdrop";
import Fade from "@material-ui/core/Fade";

import logoSrc from "../assets/logo_Dabyss.png";

const useStyles = makeStyles((theme) => ({
  header: {
    height: 80,
  },
  logo: {
    maxWidth: 60,
  },
  games: {
    padding: theme.spacing(2),
    fontWeight: "bold",
  },
  blog: {
    padding: theme.spacing(2),
    fontWeight: "bold",
  },
  contact: {
    padding: theme.spacing(2),
    fontWeight: "bold",
  },
  paper: {
    position: "absolute",
    width: "100%",
    height: "100%",
    backgroundColor: theme.palette.background.paper,
    border: "2px solid #000",
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
  },
}));

function getModalStyle() {
  const top = 50;
  const left = 50;

  return {
    top: `${top}%`,
    left: `${left}%`,
    transform: `translate(-${top}%, -${left}%)`,
  };
}

const Header = () => {
  const classes = useStyles();
  const [modalStyle] = React.useState(getModalStyle);
  const [open, setOpen] = React.useState(false);

  const handleOpen = () => {
    setOpen(true);
  };
  const handleClose = () => {
    setOpen(false);
  };

  const body = (
    <div style={modalStyle} className={classes.paper}>
      <h2 id="simple-modal-title">モーダルだよ</h2>
      <p id="simple-modal-description">
        Duis mollis, est non commodo luctus, nisi erat porttitor ligula.
      </p>
      <button onClick={handleClose}>Close</button>
    </div>
  );

  return (
    <div>
      <MuiThemeProvider theme={theme}>
        <AppBar position="static">
          <Toolbar className={classes.header}>
            <Box display="flex" justifyContent="flex-start" flexGrow={1}>
              <Link to="/">
                <Grid container alignItems="center">
                  <img src={logoSrc} className={classes.logo} />
                  <Typography variant="h6" className={classes.title}>
                    MinigameDabyss
                  </Typography>
                </Grid>
              </Link>
            </Box>
            <Hidden xsDown implementation="css">
              <Box display="flex" justifyContent="flex-end">
                <Link to="/Games">
                  <Button color="inherit" className={classes.games}>
                    ゲーム一覧
                  </Button>
                </Link>
                <Link to="/Blog">
                  <Button color="inherit" className={classes.blog}>
                    ブログ
                  </Button>
                </Link>
                <Button color="inherit" className={classes.contact}>
                  お問い合わせ
                </Button>
              </Box>
            </Hidden>
            <Hidden smUp implementation="css">
              <IconButton
                color="inherit"
                aria-label="open drawer"
                edge="start"
                onClick={handleOpen}
                className={classes.menuButton}
              >
                <MenuIcon />
              </IconButton>
            </Hidden>
          </Toolbar>
        </AppBar>
        <Modal
          open={open}
          onClose={handleClose}
          aria-labelledby="simple-modal-title"
          aria-describedby="simple-modal-description"
          closeAfterTransition
          BackdropComponent={Backdrop}
          BackdropProps={{
            timeout: 500,
          }}
        >
          {body}
        </Modal>
      </MuiThemeProvider>
    </div>
  );
};

export default Header;
