import React, { useState, useEffect } from "react";
import axios from "axios";
import marked from "marked";

import {} from "@material-ui/core";

import { makeStyles } from "@material-ui/core/styles";
import { MuiThemeProvider } from "@material-ui/core/styles";
import { theme } from "../theme";

const useStyles = makeStyles((theme) => ({}));

const GameArticle = (props) => {
  const classes = useStyles();
  const marked = require("marked");
  const markdown =
    "# heading\n" +
    "## list\n" +
    "- list1\n" +
    "- list2\n\n" +
    "## code\n" +
    "```js\n" +
    "let x = 1;\n" +
    "```\n" +
    "## table\n" +
    "| col1 | col2 |\n" +
    "---|---\n" +
    "abc|123\n" +
    "xyz|456";
  const [articles, setArticles] = useState([]);
  useEffect(() => {
    async function fetchData() {
      const res = await axios.get(
        "http://localhost:8000/api/games/" + props.match.params.id
      );
      console.log(res.data);
      setArticles(res.data);
    }
    fetchData();
  }, []);

  return (
    <div>
      <MuiThemeProvider theme={theme}>
        <div>{articles.title}</div>
        <img src={articles.sumnail}></img>
        <div id="body">
          <span dangerouslySetInnerHTML={{ __html: marked({ markdown }) }} />
        </div>
        <div>{articles.text}</div>
      </MuiThemeProvider>
    </div>
  );
};

export default GameArticle;
